import React from "react";
import { useParams } from "react-router-dom";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import Box from "@mui/material/Box";
import Typography from "@mui/material/Typography";
import Container from "@mui/material/Container";
import CssBaseline from "@mui/material/CssBaseline";
import Avatar from "@mui/material/Avatar";
import Button from "@mui/material/Button";
import Link from "@mui/material/Link";
import NewPurchaseItemForm from "./NewPurchaseItemForm";
import moment from "moment";

function Copyright(props) {
  return (
    <Typography
      variant="body2"
      color="text.secondary"
      align="center"
      {...props}
    >
      {"Copyright © "}
      <Link color="inherit" href="https://mui.com/">
        Pantry
      </Link>{" "}
      {new Date().getFullYear()}
      {"."}
    </Typography>
  );
}

function NewPurchaseForm(props) {
  const food = props.food;
  const updateInv = props.updateInv;
  const date = moment();
  const currentDate = date.format("YYYY-MM-DD");

  //   let date = purchase.date.toString().slice(0, 10);
  console.log(date);

  const pantryTheme = createTheme();

  return (
    <ThemeProvider theme={pantryTheme}>
      <Container component="main" maxWidth="xs">
        <CssBaseline />
        <Box
          sx={{
            marginTop: 8,
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          <Box
            component="form"
            onSubmit={addPurchase}
            sx={{ mb: 1, fontSize: "2rem", ml: 2, mt: 2 }}
          >
            <TextField
              name="date"
              required
              InputLabelProps={{ shrink: true }}
              type="date"
              label="Date"
              id="date"
              value={formDate}
              onChange={handleDateChange}
            />
            <Button onClick={addPurchase}>
              <span className="material-symbols-outlined">add</span>
            </Button>
          </Box>

          <Avatar sx={{ m: 1, bgcolor: "secondary.main" }}>
            <span className="material-symbols-outlined">nutrition</span>
          </Avatar>
          <Typography component="h1" variant="h5" sx={{ textAlign: "center" }}>
            Add Items to Purchase Order
          </Typography>
          <Typography component="h2" sx={{ textAlign: "center" }}>
            {date}
          </Typography>
          <Box sx={{ mt: 1 }}>
            {food.map((f) => (
              <NewPurchaseItemForm
                key={f.foodId}
                purchaseId={purchaseId}
                foodId={f.foodId}
                name={f.name}
                inventory={f.inventory}
                updateInv={updateInv}
              />
            ))}
            <Button fullWidth variant="contained" sx={{ mt: 3, mb: 2 }}>
              Submit
            </Button>
          </Box>
        </Box>
        <Copyright sx={{ mt: 8, mb: 4 }} />
      </Container>
    </ThemeProvider>
  );
}

export default NewPurchaseForm;
