  //     return (
  //       <div>
  //         <CircularProgress color="secondary" />;
  //       </div>
  //     );
  //   } else {
  //     console.log(order);
  return (
    <TableRow onClick={() => handleRowClick()} key={orderId}>
      <TableCell>{orderId}</TableCell>
      <TableCell>{clientId}</TableCell>
      <TableCell>{date}</TableCell>
    </TableRow>
  );
}
export default OrderCard;
