import React, { useState } from "react";
import TextField from "@mui/material/TextField";
import PantryApi from "../pantryApi";
import Button from "@mui/material/Button";
import Checkbox from "@mui/material/Checkbox";
import FormControlLabel from "@mui/material/FormControlLabel";
import "./orders.css";
import MenuItem from "@mui/material/MenuItem";
import Grid from "@mui/material/Grid";
import { Formik, Form, ErrorMessage } from "formik";
import * as yup from "yup";
import { Typography } from "@mui/material";

function NewOrderItemForm({
  foodId,
  name,
  orderId,
  inventory,
  total,
  setTotal,
  updateInv,
}) {
  const [disabled, setDisabled] = useState(false);
  const [checked, setChecked] = useState(false);
  const [checkDisabled, setCheckDisabled] = useState(false);
  const [isAdded, setIsAdded] = useState(false);

  const handleCheckChange = (event) => {
    setChecked(event.target.checked);
  };

  // const [quantity, setQuantity] = useState("");
  const [formErrors, setFormErrors] = useState(null);

  //   }

  const initialValue = {
    quantity: "",
  };

  const orderValidation = yup.object().shape({
    quantity: yup
      .number()
      .max(2, "Maximum of 2 per item")
      .max(12 - total, "Can't have more than 12 items")
      .max(inventory, "Not enough inventory remaining"),
  });

  async function addItem(values, props) {
    // event.preventDefault();
    const quantNum = parseInt(values.quantity);
    console.log(values.quantity);
    console.log(values);
    // if (total + quantNum > 12) {
    //   setFormErrors("You've selected more than 12 items");
    //   // setQuantity(0);
    //   return total;
    // } else if (quantNum > inventory) {
    //   setFormErrors("No inventory for this item. Select another item.");
    //   // setQuantity(0);
    //   setDisabled(true);
    //   setCheckDisabled(true);
    // } else {
    try {
      await PantryApi.addOrderItems(orderId, foodId, {
        quantity: values.quantity,
      });
      setIsAdded(true);
      setCheckDisabled(true);
      setTotal(total + quantNum);

      try {
        await updateInv(foodId, -Math.abs(quantNum));
      } catch (err) {
        setFormErrors(err);
      }
    } catch (err) {
      setFormErrors(err);
    }
  }

  async function removeOrderItem(values, props) {
    // event.preventDefault();
    const quantNum = parseInt(values.quantity);
    let res = await PantryApi.removeOrderItem(orderId, foodId);

    if (res) {
      setIsAdded(false);
      setCheckDisabled(false);
      // setQuantity("");
      setTotal(total - quantNum);

      let update = await updateInv(foodId, quantNum);
    } else {
      //   setFormErrors(orderItem.err);
    }
  }

  // const handleChange = (evt) => {
  //   setQuantity(evt.target.value);
  // };

  //   async function addItem(event) {
  //     event.preventDefault();

  // // within food array find food item where key in object = (foodId)
  // for (let key in formData) {
  //   if (formData[key]) {
  //     const quantity = formData[key];
  //     const item = food.find((f) => f.foodId === parseInt(key));

  //     if (quantity > item.inventory) {
  //       // ERROR MESSAGE HERE
  //     } else {
  //       const foodItem = await PantryApi.addOrderItems(order.orderId, key, {
  //         quantity,
  //       });

  //       if (foodItem) {
  //         history.push("/clients");
  //       }
  //     }
  //   }
  // }

  return (
    <React.Fragment>
      <Formik
        initialValues={initialValue}
        validationSchema={orderValidation}
        // onSubmit={addItem}
      >
        {(props) => {
          const { quantity } = props.values;
          return (
            <Form>
              <Grid container spacing={3} sx={{ mb: 3 }}>
                <Grid item xs={!checked ? 12 : 5}>
                  <FormControlLabel
                    control={
                      <Checkbox
                        checked={checked}
                        disabled={checkDisabled}
                        name={name}
                        onChange={handleCheckChange}
                      />
                    }
                    label={name}
                  />
                </Grid>
                <Grid item xs={3}>
                  {checked ? (
                    <TextField
                      name="quantity"
                      disabled={disabled}
                      label="#"
                      autoFocus
                      id="quantity"
                      type="integer"
                      value={quantity}
                      onChange={props.handleChange}
                      onBlur={props.handleBlur}
                      helperText={<ErrorMessage name="quantity" />}
                      error={props.errors.quantity && props.touched.quantity}
                    />
                  ) : (
                    ""
                  )}
                </Grid>

                <Grid item xs={4}>
                  {checked && !isAdded ? (
                    <Button size="small">Add to Cart</Button>
                  ) : (
                    ""
                  )}

                  {isAdded ? (
                    <Button size="small" onClick={removeOrderItem}>
                      <span className="material-symbols-outlined">delete</span>
                    </Button>
                  ) : (
                    ""
                  )}
                </Grid>

                <Grid item xs={12} sx={{ textAlign: "center", color: "red" }}>
                  {formErrors ? (
                    <span>
                      <Typography>{formErrors}</Typography>
                    </span>
                  ) : (
                    <span></span>
                  )}
                </Grid>
              </Grid>
            </Form>
          );
        }}
      </Formik>
    </React.Fragment>
  );
}

export default NewOrderItemForm;
