import React, { useState, useEffect } from "react";
import { BrowserRouter } from "react-router-dom";
import UserContext from "./auth/UserContext";
import Nav from "./routes-nav/Nav";
import Routes from "./routes-nav/Routes";
import PantryApi from "./pantryApi";
import "./App.css";
import jwt from "jsonwebtoken";
import AuthContext from "./auth/AuthContext";

// Key name for storing token in localStorage for "remember me" re-login
export const TOKEN = "pantry-token";

function App() {
  const [currentUser, setCurrentUser] = useState(null);
  const [token, setToken] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [food, setFood] = useState([]);
  const [inventory, setInventory] = useState([]);
  const [newFood, setNewFood] = useState([]);

  // upon loading call API to get the current user
  useEffect(() => {
    console.debug("App useEffect loadUserInfo", "token=", token);

    async function getUser() {
      try {
        let user = await PantryApi.getCurrentUser(currentUser.username);
        setCurrentUser(user);
      } catch (err) {
        setCurrentUser(null);
      }
    }
    getUser();
  }, [token]);

  // upon loading, call API to get a list of all food
  useEffect(() => {
    async function getFood() {
      let food = await PantryApi.getFood();
      setFood(food);
    }
    getFood();
  }, [inventory, newFood]);

  console.log(food);

  async function updateInv(foodId, quantNum) {
    try {
      let update = await PantryApi.updateInv(foodId, quantNum);
      setInventory(update);
      return update;
    } catch (err) {
      return err;
    }
  }

  async function addNewFood(data) {
    try {
      let food = await PantryApi.addFood(data);
      setNewFood(food);
      return food;
    } catch (err) {
      return err;
    }
  }

  // function to handle login and set and save token to local storage
  async function login(data) {
    try {
      let token = await PantryApi.login(data);
      setCurrentUser({ username: data.username });
      setIsLoggedIn(true);
      setToken(token);
      localStorage.setItem("TOKEN", token);
      return { success: true };
    } catch (err) {
      console.error("login failed", err);
      return { success: false, err };
    }
  }

  // function to handle user logout. Clear token from storage
  function logout() {
    localStorage.removeItem("TOKEN");
    setIsLoggedIn(false);
  }

  // function to handle new user signup
  async function signup(data) {
    try {
      let token = await PantryApi.signup(data);
      setCurrentUser({ username: data.username });
      setIsLoggedIn(true);
      localStorage.setItem("TOKEN", token);
      setToken(token);
      return { success: true };
    } catch (err) {
      return { success: false, err };
    }
  }

  return (
    <BrowserRouter>
      {/* <AuthContext.Provider value={{ isLoggedIn }}> */}
      <UserContext.Provider value={{ currentUser }}>
        <div className="App">
          {/* {currentUser ? <Nav logout={logout} /> : ""} */}
          <Routes
            updateInv={updateInv}
            food={food}
            addNewFood={addNewFood}
            login={login}
            signup={signup}
          />
        </div>
      </UserContext.Provider>
      {/* </AuthContext.Provider> */}
    </BrowserRouter>
  );
}

export default App;
