import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import Box from "@mui/material/Box";
import Typography from "@mui/material/Typography";
import Container from "@mui/material/Container";
import CssBaseline from "@mui/material/CssBaseline";
import Avatar from "@mui/material/Avatar";
import Button from "@mui/material/Button";
import Link from "@mui/material/Link";
import Checkbox from "@mui/material/Checkbox";
// import NewOrderItemForm from "./NewOrderItemForm";

function Copyright(props) {
  return (
    <Typography
      variant="body2"
      color="text.secondary"
      align="center"
      {...props}
    >
      {"Copyright © "}
      <Link color="inherit" href="https://mui.com/">
        Pantry
      </Link>{" "}
      {new Date().getFullYear()}
      {"."}
    </Typography>
  );
}

function NewOrderForm(props) {
  const location = useLocation();
  const food = props.food;
  const order = location.state.params;
  // let date = order.date.toString().slice(0, 10);
  // console.log(date);

  const [formData, setFormData] = useState(initialState);
  const [formErrors, setFormErrors] = useState([]);
  const [checked, setChecked] = useState(true);

  const pantryTheme = createTheme();

  const handleChange = (event) => {
    setChecked(event.target.checked);
  };

  return (
    <ThemeProvider theme={pantryTheme}>
      <Container component="main" maxWidth="xs">
        <CssBaseline />
        <Box
          sx={{
            marginTop: 8,
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          <Avatar sx={{ m: 1, bgcolor: "secondary.main" }}>
            <span className="material-symbols-outlined">nutrition</span>
          </Avatar>
          <Typography component="h1" variant="h5" sx={{ textAlign: "center" }}>
            Pantry Menu
          </Typography>
          <Typography component="h2" sx={{ textAlign: "center" }}>
            {/* {date} */}
          </Typography>
          <Typography component="h3" sx={{ textAlign: "center" }}>
            Clients can choose up to 12 items. Limit of 2 per item, except where
            noted.
          </Typography>
          <Box
            component="form"
            // onSubmit={handleSubmit}
            noValidate
            sx={{ mt: 1 }}
          >
            <Checkbox
              required
              fullWidth
              inputProps={{ "aria-label": "controlled" }}
              checked={checked}
              id="chicken"
              label="Chicken"
              name="Chicken"
              onChange={handleChange}
              value={formData.firstName}
              autoFocus
            />

            <Button fullWidth variant="contained" sx={{ mt: 3, mb: 2 }}>
              Submit
            </Button>
          </Box>
        </Box>
        <Copyright sx={{ mt: 8, mb: 4 }} />
      </Container>
    </ThemeProvider>
  );
}

export default NewOrderForm;
