import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import TableRow from "@mui/material/TableRow";
import TableCell from "@mui/material/TableCell";
import TextField from "@mui/material/TextField";
import Box from "@mui/material/Box";
import "./purchases.css";
import PantryApi from "../pantryApi";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import Checkbox from "@mui/material/Checkbox";
import FormControlLabel from "@mui/material/FormControlLabel";

function NewOrderItemForm({ foodId, name, orderId, inventory }) {
  console.log(foodId, name, inventory);
  const history = useHistory();
  const initialCheckState = {
    foodName: false,
  };

  const [checked, setChecked] = useState(initialCheckState);

  const handleCheckChange = (event) => {
    setChecked({ ...checked, [event.target.name]: event.target.checked });
  };
  //   const [items, setItems] = useState(initialState);
  //   const [formErrors, setFormErrors] = useState(null);

  //   }
  //   async function handleSubmit(event) {
  //     event.preventDefault();

  //     let orderItem = await PantryApi.addOrderItems(purchaseId, foodId, items);

  //     if (orderItem) {
  //       setIsDisabled(true);
  //     } else {
  //       setFormErrors(purchaseItem.err);
  //     }
  //   }

  //   const handleChange = (evt) => {
  //     const { name, value } = evt.target;
  //     setItems((data) => ({
  //       ...data,
  //       [name]: value,
  //     }));
  //   };
  //   console.log(items);
  //   console.log(isDisabled);

  return (
    <React.Fragment>
      <Box
        sx={{
          marginTop: 2,
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        {/* <Box component="form" onSubmit={handleSubmit} noValidate sx={{ mt: 1 }}> */}
        <Box
          sx={{
            border: 1,
            width: "100%",
          }}
        >
          <Typography sx={{ textAlign: "center", bgcolor: "primary.main" }}>
            {name}
          </Typography>
        </Box>
        <FormGroup aria-label="position" row>
          <Box sx={{ display: "flex", flexDirection: "row" }}>
            <FormControlLabel
              control={
                <Checkbox
                  checked={foodName}
                  id={foodName}
                  name={foodName}
                  onChange={handleCheckChange}
                />
              }
              label=""
            />
            {checked.chicken ? (
              <TextField
                margin="normal"
                fullWidth
                id="chicken"
                name={foodId}
                label="# Chicken"
                onChange={handleChange}
                autoFocus
              />
            ) : (
              ""
            )}
          </Box>
        </FormGroup>
      </Box>
    </React.Fragment>
  );
}

export default NewOrderItemForm;
