import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Box from "@mui/material/Box";
import "./purchases.css";
import PantryApi from "../pantryApi";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import { TableFooter } from "@mui/material";

function PurchaseItemCard({ food }) {
  console.log(food);
  let quantityTotal = 0;
  let valTotal = 0;
  for (let i = 0; i < food.length; i++) {
    console.log(food[i].quantity);
    quantityTotal = quantityTotal + food[i].quantity;
    valTotal = valTotal + food[i].quantity * food[i].pricePerUnit;
  }

  console.log(valTotal, quantityTotal);

  return (
    <React.Fragment>
      <Box
        sx={{
          marginTop: 2,
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <Table size="small">
          <TableHead>
            <TableRow>
              <TableCell>Name</TableCell>
              <TableCell>Quantity</TableCell>
              <TableCell>Price Per Unit</TableCell>
              <TableCell>Total</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {food.map((f) => (
              <TableRow key={f.foodId}>
                <TableCell>{f.name}</TableCell>
                <TableCell>{f.quantity}</TableCell>
                <TableCell>{f.pricePerUnit}</TableCell>
                <TableCell s>
                  {(f.quantity * f.pricePerUnit).toFixed(2)}
                </TableCell>
              </TableRow>
            ))}
            <TableRow>
              <TableCell colSpan={1}>Subtotal</TableCell>
              <TableCell align="right">{valTotal}</TableCell>
            </TableRow>

            {/*             


              <TableRow>
                <TableCell colSpan={1} />
                <TableCell colSpan={1} />
                <TableCell colSpan={1}>{quantityTotal}</TableCell>
                <TableCell colSpan={1}>{valTotal}</TableCell>
              </TableRow>
            </TableFooter> */}
          </TableBody>
        </Table>
      </Box>
    </React.Fragment>
  );
}

export default PurchaseItemCard;
